import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js'; // Import necessary Chart.js components
import './App.css';

// Register the components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const App = () => {
    const [walletAddress, setWalletAddress] = useState('');
    const [voteOptions, setVoteOptions] = useState([
        { id: 1, name: 'Best Programming Language to Learn in 2024', votes: 0 },
        { id: 2, name: 'Most Impactful Tech Innovation of 2024', votes: 0 },
        { id: 3, name: 'Best Framework for Web Development', votes: 0 },
    ]);
    const [selectedOption, setSelectedOption] = useState(null);

    const connectWallet = async () => {
        // Connect wallet logic...
    };

    const handleVote = (id) => {
        setVoteOptions(prevOptions =>
            prevOptions.map(option =>
                option.id === id ? { ...option, votes: option.votes + 1 } : option
            )
        );
        setSelectedOption(id);
    };

    const data = {
        labels: voteOptions.map(option => option.name),
        datasets: [
            {
                label: 'Votes',
                data: voteOptions.map(option => option.votes),
                backgroundColor: ['rgba(255, 99, 132, 0.2)', 'rgba(54, 162, 235, 0.2)', 'rgba(255, 206, 86, 0.2)'],
                borderColor: ['rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', 'rgba(255, 206, 86, 1)'],
                borderWidth: 1,
            },
        ],
    };

    return (
        <div className="App">
            <h1>Decentralized Voting Platform</h1>
            <button onClick={connectWallet}>Connect Wallet</button>
            {walletAddress && <p>Connected Wallet: {walletAddress}</p>}

            <h2>Voting Options</h2>
            <ul>
                {voteOptions.map(option => (
                    <li key={option.id}>
                        {option.name} - Votes: {option.votes}
                        <button onClick={() => handleVote(option.id)}>Vote</button>
                    </li>
                ))}
            </ul>

            <h2>Voting Results</h2>
            <Bar data={data} />

            {selectedOption && <p>Thank you for voting for {voteOptions.find(option => option.id === selectedOption).name}!</p>}

            <h2>About This Voting Platform</h2>
            <p>
                This decentralized voting platform allows users to vote on various technology-related topics. 
                Your vote matters, and every vote helps us understand the preferences and opinions of the community.
                Join us in shaping the future of technology!
            </p>
        </div>
    );
};

export default App;
