import React from 'react';
import './App.css';

function App() {
  const connectWallet = async () => {
    // This function will be used to connect the user's wallet
    alert("Wallet connection not implemented yet");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Decentralized Voting Platform</h1>

        <button onClick={connectWallet}>
          Connect Wallet
        </button>

        <h2>Voting Categories</h2>
        <ul>
          <li>Best Project</li>
          <li>Best Presentation</li>
          <li>Most Innovative</li>
        </ul>

        <button>
          Cast Vote
        </button>
      </header>
    </div>
  );
}

export default App;
