import React from 'react';
import './App.css';
import { FaVoteYea, FaWallet } from 'react-icons/fa';

function App() {
  const connectWallet = async () => {
    alert("Wallet connection not implemented yet");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Decentralized Voting Platform</h1>

        <button className="wallet-btn" onClick={connectWallet}>
          <FaWallet /> Connect Wallet
        </button>

        <div className="voting-section">
          <h2>Voting Categories</h2>
          <ul>
            <li><FaVoteYea /> Best Project</li>
            <li><FaVoteYea /> Best Presentation</li>
            <li><FaVoteYea /> Most Innovative</li>
          </ul>
        </div>

        <button className="cast-vote-btn">
          Cast Vote
        </button>
      </header>
    </div>
  );
}

export default App;
