import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
    const [walletAddress, setWalletAddress] = useState('');
    const [options] = useState(['Option A', 'Option B', 'Option C']);
    const [votes, setVotes] = useState({ 'Option A': 0, 'Option B': 0, 'Option C': 0 });

    const connectWallet = async () => {
        if (!window.aptos) {
            alert('Please install an Aptos wallet!');
            return;
        }

        try {
            const { address } = await window.aptos.connect();
            setWalletAddress(address);
        } catch (error) {
            console.error("Wallet connection error:", error);
        }
    };

    const disconnectWallet = () => {
        setWalletAddress('');
    };

    const castVote = (option) => {
        setVotes({ ...votes, [option]: votes[option] + 1 });
    };

    return (
        <div className="container text-center mt-5">
            <h1 className="mb-4">Decentralized Voting Platform</h1>
            {walletAddress ? (
                <>
                    <p>Connected Wallet: <strong>{walletAddress}</strong></p>
                    <button className="btn btn-danger" onClick={disconnectWallet}>Disconnect Wallet</button>

                    <h3 className="mt-4">Vote for Your Option:</h3>
                    {options.map((option) => (
                        <div key={option} className="mt-2">
                            <button className="btn btn-success" onClick={() => castVote(option)}>
                                Vote for {option}
                            </button>
                        </div>
                    ))}

                    <h4 className="mt-4">Current Votes:</h4>
                    <ul className="list-group">
                        {Object.entries(votes).map(([option, count]) => (
                            <li key={option} className="list-group-item">
                                {option}: {count} votes
                            </li>
                        ))}
                    </ul>

                    <div className="mt-5">
                        <h4>Voting Instructions:</h4>
                        <p>Please select your preferred option by clicking the corresponding button. Your vote will be counted and displayed below.</p>
                        <p>Make sure your wallet is connected to cast a vote!</p>
                    </div>
                </>
            ) : (
                <button className="btn btn-primary" onClick={connectWallet}>Connect Wallet</button>
            )}
        </div>
    );
}

export default App;
